module.exports = getPasswordOfUser;

function getPasswordOfUser(Name,callback){
    var config = this.config;

    var sql = 'SELECT ID FROM PersonalInformation WHERE Name="' + Name + '"';

    config.modules['saferman'].sql(sql,function(results){
        if(results[0] != undefined){
            sql = 'SELECT Shadow FROM ShadowTble WHERE ID=' + results[0].toString();

            config.modules['saferman'].sql(sql,function(results){
                console.log(true);
                executeCallback(results[0]);
            })

        }else{
            console.log(false);
            return false;
        }

        function executeCallback(argument){
            if(callback!=undefined)
                callback(argument);
        }
    });
}

