module.exports = initUser;

var NextUsableID = 0;

function initUser(Name,callback){
    var config = this.config;

    var sql = 'INSERT INTO PersonalInformation (ID,Name) VALUE (' + getNextUsableID() + ",'" + Name + "')";
    updateNextUsableID();

    config.modules['saferman'].sql(sql,executeCallback);

    function executeCallback(){
        if(callback!=undefined)
            callback();
    }
}


function getNextUsableID(){
    return NextUsableID;
}

function updateNextUsableID(){
    NextUsableID++;
}
