module.exports = Add;

function Add(ID,right,callback){

    var config = this.config;

    if(isRightStringLegal(right) == false){
        executeCallback(false);
        return;
    }

    var sql = 'SELECT Rights FROM RightsTable WHERE ID=' + ID;
    config.modules['saferman'].sql(sql,handleResultsOfSQL);


    function executeCallback(argumentOfCallback){
        if(callback!=undefined)
            callback(argumentOfCallback);
    }

    function isRightStringLegal(right){
        if(right != 'publish'
            && right != 'answer'
            && right != 'view'
            && right != 'view_personal_infromation'
        ){
            return false;
        }else{
            return true;
        }
    }

    function handleResultsOfSQL(sqlResults){

        var RegExpToMatch = new RegExp(right);
        var isUserHaveRight = (RegExpToMatch.exec(sqlResults[0].Rights)!=null);

        if(isUserHaveRight){
            executeCallback(false);
        }else{
            addRightOfUser();
        }

        function addRightOfUser(){
            var sql = "UPDATE RightsTable SET Rights='" + sqlResults[0].Rights + "|" + right + "' WHERE ID=0" ;
            config.modules['saferman'].sql(sql,executeCallback);
        }

    }
}

