//test environment init
var expect = require('chai').expect;
var directory = '../../';

var config = {
    modules: []
};

//module load area
config.modules['rights_management'] = (require(directory + './rights_management'))(config);
config.modules['saferman'] = (require(directory + './saferman'))(config);

describe('Add',function(){

    before(function(done){
        config.modules['saferman'].sql('DELETE FROM RightsTable',function(){
            config.modules['saferman'].sql('INSERT INTO RightsTable (ID,Rights) VALUE (0,"|publish|view")',function(){
                done();
            });
        });
    });

    //test_1
    it('add right answer',function(done){
        config.modules['rights_management'].Add(0,'answer',function(){
            var sql_string = 'SELECT Rights FROM RightsTable WHERE ID=0';

            config.modules['saferman'].sql(sql_string,function(results){
                var match = new RegExp('answer');
                var value = (match.exec(results[0].Rights)!=null);

                expect(value).to.be.a('boolean');
                expect(value).to.be.equal(true);

                done();
            });
        });

    });

    //test_1
    it('add illegal right',function(done){
        config.modules['rights_management'].Add(0,'ppt',function(){
            var sql_string = 'SELECT Rights FROM RightsTable WHERE ID=0';

            config.modules['saferman'].sql(sql_string,function(results){
                var match = new RegExp('ppt');
                var value = (match.exec(results[0].Rights)==null);

                expect(value).to.be.a('boolean');
                expect(value).to.be.equal(true);

                done();
            });
        });

    });

})
