module.exports = getScoreByUserID;

function getScoreByUserID(userID,callback){

    let config = this.config;
    let sql = 'SELECT score FROM AnswerTable WHERE userID=' + userID;

    config.modules['saferman'].sql(sql,function(results){
        if(results.length != 0){
            executeCallback(results[0].score);
        }else{
            executeCallback(0);
        }
    });


    function executeCallback(argumentOfCallback){
        if(callback!=undefined)
            callback(argumentOfCallback);
    }
}
